mapper.select:
SELECT M.MEMBER_NO,
CASE WHEN SUB.SUBSCRIBE_END IS NOT NULL AND SUB.SUBSCRIBE_END < CURRENT_DATE THEN '0' ELSE COALESCE(TO_CHAR(SUB.SUBSCRIBE_LEVEL), '0') END AS SUBSCRIBE_LEVEL, COALESCE(M.MILEAGE, 0) AS MILEAGE
FROM MEMBER M LEFT JOIN SUBSCRIBE SUB ON M.MEMBER_NO = SUB.MEMBER_NO WHERE M.MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER
SET MILEAGE = MILEAGE+2000
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BOARD_CODE = #{boardCode}
AND BOARD_ST = 'N'
