mapper.insert:
INSERT INTO IMAGE SELECT A.* FROM (
SELECT #{img.boardNo} BR_NO, 4 IMG_CD, #{img.imagePath} IMG_PATH, #{img.imageRename} IMG_RENAME, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageOrder} IMG_LEVEL FROM DUAL
)A

mapper.select:
SELECT MAX(BOARD_NO) AS PREVIOUS FROM(
SELECT BOARD_NO FROM BOARD

WHERE BOARD_NO < #{boardNo}

AND BOARD_CODE=4
AND BOARD_ST = 'N'
)

SELECT MIN(BOARD_NO) AS PREVIOUS FROM(
SELECT BOARD_NO FROM BOARD

WHERE BOARD_NO > #{boardNo}

AND BOARD_CODE=4
AND BOARD_ST = 'N'
)


		SELECT COUNT(*) FROM
		"LIKE"
		WHERE BR_NO = #{boardNo}
		AND MEMBER_NO = #{memberNo}
        AND LIKE_CODE = 1

mapper.delete:
DELETE FROM "LIKE" 
WHERE MEMBER_NO = #{memberNo}
AND BR_NO = #{boardNo}
AND LIKE_CODE = 1
