mapper.insert:
INSERT INTO IMAGE SELECT A.* FROM (
SELECT #{img.boardNo} BR_NO, 4 IMG_CD, #{img.imagePath} IMG_PATH, #{img.imageReName} IMG_RENAME, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageOrder} IMG_LEVEL FROM DUAL
)A

mapper.select:
SELECT MAX(BOARD_NO) AS PREVIOUS FROM(
SELECT BOARD_NO FROM BOARD

WHERE BOARD_NO < #{boardNo}

AND BOARD_CODE=4
)
